#!/bin/bashmkdir -p public;npm init -y;npm i --save async request pg;node --max-old-space-size=8192 ./download.js;sleep 10;cd public && csplit ivi.xml '/^<contents>$/' '{*}';echo "</xml>" >> xx00;sed  -i '1i <?xml version="1.0" encoding="UTF-8" ?><xml>' xx01;cd ../;git clone https://github.com/Cheedoong/xml2json;cd xml2json && make;./xml2json ../public/xx00 > ../public/categories.json;./xml2json ../public/xx01 > ../public/movies.json;cd ../; sleep 10;PGUSER="postgres" PGHOST="IP" PGPASSWORD="password" PGDATABASE="pleer" PGPORT=38397 node --max-old-space-size=8192 ./upload.js;